import React from 'react';
import './App.css';

class Clock extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            date: new Date(),
            time: ''
        }

        this.updateTime = this.updateTime.bind(this);
    }

    componentDidMount(){
        this.interval = setInterval(this.updateTime, 1000);
    }

    updateTime(){
        let hour = this.state.date.getHours();
        let minute = this.state.date.getMinutes();
        let seconds = this.state.date.getSeconds();
        let period = ' AM';
        this.setState({date: new Date(), time: hour + ':' + minute + ':' + seconds + period});
    }

    render(){
        return(
            <div>
                {this.state.time}
            </div>
        );
    }
}

function App() {
  return (
    <div className="App">
      <Clock />
    </div>
  );
}

export default App;
