import React from 'react';
import './App.css';

class Clock extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            time: ''
        }

        this.updateTime = this.updateTime.bind(this);
    }

    componentDidMount(){
        this.interval = setInterval(this.updateTime, 1000);
    }

    updateTime(){
        let date = new Date();
        let hour = date.getHours();
        let minute = date.getMinutes();
        let seconds = date.getSeconds();
        if(seconds < 10){
            seconds = '0' + seconds;
        }
        let period = ' AM';

        if(hour >= 12){
            period = ' PM'

            if(hour > 12){
                hour -= 12;
            }
        }
        this.setState({time: hour + ':' + minute + ':' + seconds + period});
    }

    render(){
        return(
            <div id='clock'>
                {this.state.time}
            </div>
        );
    }
}

function App() {
  return (
    <div className="App">
      <Clock />
    </div>
  );
}

export default App;
