import React from 'react';
import './App.css';

class Clock extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            time: ''
        }

        this.updateTime = this.updateTime.bind(this);
    }

    componentDidMount(){
        this.interval = setInterval(this.updateTime, 1000);
    }

    updateTime(){
        let date = new Date();
        let hours = date.getHours();
        let minutes = date.getMinutes();
        if(minutes < 10){
            minutes = '0' + minutes;
        }
        let seconds = date.getSeconds();
        if(seconds < 10){
            seconds = '0' + seconds;
        }
        let period = ' AM';

        if(hours >= 12){
            period = ' PM'

            if(hours > 12){
                hours -= 12;
            }
        }
        if(hours < 10){
            hours = '0' + hours;
        }

        this.setState({time: hours + ':' + minutes + ':' + seconds + period});
    }

    render(){
        return(
            <div id='clock'>
                {this.state.time}
            </div>
        );
    }
}

class Weather extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            temperature: '',
            wind: '',
            forecast: '',
            test: ''
        }
    }

    componentDidMount(){
        this.setState({request: new XMLHttpRequest()});
        this.request.open('GET', 'https://api.weather.gov/gridpoints/LIX/61,92/forecast');
        this.request.send();
        console.log(this.request.responseText);
        this.setState = {
            test: this.request.responseText
        }
    }

    render(){
        return(
            <div id="weather">
                <p>{this.state.test}</p>
            </div>
        )
    }
}

function App() {
    return (
    <div className="App">
        <div id='grid'>
            <Clock />
            <Weather />
        </div>
    </div>
    );
}

export default App;
