import React from 'react';
import './App.css';

class Clock extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            time: ''
        }

        this.updateTime = this.updateTime.bind(this);
    }

    componentDidMount(){
        this.interval = setInterval(this.updateTime, 1000);
    }

    updateTime(){
        let date = new Date();
        let hours = date.getHours();
        let minutes = date.getMinutes();
        if(minutes < 10){
            minutes = '0' + minutes;
        }
        let seconds = date.getSeconds();
        if(seconds < 10){
            seconds = '0' + seconds;
        }
        let period = ' AM';

        if(hours >= 12){
            period = ' PM'

            if(hours > 12){
                hours -= 12;
            }
        }
        if(hours < 10){
            hours = '0' + hours;
        }

        this.setState({time: hours + ':' + minutes + ':' + seconds + period});
    }

    render(){
        return(
            <div id='grid'>
                <div id='clock'>
                    {this.state.time}
                </div>
            </div>
        );
    }
}

class Weather extends React.Component{
    constuctor(){
        this.state = {
            temperature: '',
            wind: '',
            forecast: ''
        }
    }

    render(){
        return(
            <div>
                <p>this.state.temperature + ' ' + this.state.wind + ' ' + this.state.forecast</p>
            </div>
        )
    }
}

function App() {
    return (
    <div className="App">
        <Clock />
    </div>
    );
}

export default App;
